```javascript
// src/App.js
import React, { useState, useEffect, useCallback } from 'react';
import PokemonTypeFilter from './components/PokemonTypeFilter';
import PokemonCard from './components/PokemonCard';
import { fetchTotalCount, fetchPokemonTypes, fetchPokemonDataByType } from './helpers/fetchData';
import './App.css';

const App = () => {
  const [pokemonData, setPokemonData] = useState([]);
  const [pokemonTypes, setPokemonTypes] = useState([]);
  const [selectedType, setSelectedType] = useState(null);
  const [typePages, setTypePages] = useState({});
  const [totalCount, setTotalCount] = useState(0);
  const [loading, setLoading] = useState(false);
  const pokemonPerPage = 20;

  useEffect(() => {
    const fetchData = async () => {
      setTotalCount(await fetchTotalCount());
      setPokemonTypes(await fetchPokemonTypes());
    };

    fetchData();
  }, []);

  const fetchAndSetPokemonData = useCallback(
    async (type = null, page = 1) => {
      setLoading(true);
      const fetchedPokemon = await fetchPokemonDataByType(type, page, pokemonPerPage);
      setPokemonData((prevData) => [...prevData, ...fetchedPokemon]);
      setLoading(false);
    },
    [pokemonPerPage]
  );

  useEffect(() => {
    if (!selectedType) {
      fetchAndSetPokemonData();
    }
  }, [fetchAndSetPokemonData, selectedType]);

  const filterByType = async (type) => {
    setSelectedType(type);
    setPokemonData([]);

    if (!typePages[type]) {
      setTypePages((prevPages) => ({ ...prevPages, [type]: 1 }));
    }

    await fetchAndSetPokemonData(type, typePages[type]);
  };

  const clearFilter = () => {
    setSelectedType(null);
    setPokemonData([]);
    fetchAndSetPokemonData();
  };

  const handleScroll = useCallback(async () => {
    if (
      window.innerHeight + document.documentElement.scrollTop !==
        document.documentElement.offsetHeight ||
      loading
    )
      return;

    if (selectedType) {
      setTypePages((prevPages) => {
        const updatedPages = { ...prevPages, [selectedType]: prevPages[selectedType] + 1 };
        fetchAndSetPokemonData(selectedType, updatedPages[selectedType]);
        return updatedPages;
      });
    } else {
      fetchAndSetPokemonData();
    }
  }, [loading, selectedType, fetchAndSetPokemonData]);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [handleScroll]);

  return (
    <div className="App">
      <h1>Pokédex</h1>
      <PokemonTypeFilter
        pokemonTypes={pokemonTypes}
        filterByType={filterByType}
        clearFilter={clearFilter}
      />
      {pokemonData.map((pokemon) => (
        <PokemonCard key={pokemon.id} pokemon={pokemon} />
      ))}
      {loading && <p>Loading...</p>}
    </div>
  );
};

export default App;
```

