  let currentDeck = {...deck};
  let drawnCards: Card[] = [];

  while (drawnCards.length < numCards) {
    if (currentDeck.drawPile.length === 0) {
      currentDeck = {
        drawPile: _.shuffle(currentDeck.discardPile),
        discardPile: [],
