function calculateRepaymentSchedule($totalDisbursed, $normalInterestRate, $equatedInstallment, $interestAccrualMethod, $repaymentSchedule, $transactions)
{
    $daysInYear = $interestAccrualMethod === 'rate/360' ? 360 : 365;
    $combinedSchedule = [];
    $remainingPrincipal = $totalDisbursed;

    // Filter transactions
    $filteredTransactions = array_filter($transactions, function ($transaction) {
        return in_array($transaction['transactionName'], ['Amendment', 'Prepayment', 'AdvanceRepayment']);
    });

    // Combine repayment schedule and transactions
    foreach ($repaymentSchedule as $schedule) {
        $combinedSchedule[] = [
            'valueDate' => $schedule['valueDate'],
            'emi' => $equatedInstallment,
            'type' => 'Repayment',
        ];
    }

    foreach ($filteredTransactions as $transaction) {
        $combinedSchedule[] = [
            'valueDate' => $transaction['valueDate'],
            'emi' => $transaction['repaymentAmount'],
            'type' => $transaction['transactionName'],
        ];
    }

    // Sort the combined schedule by value date
    usort($combinedSchedule, function ($a, $b) {
        return strtotime($a['valueDate']) <=> strtotime($b['valueDate']);
    });

