gsi_key_schema = [
    {'AttributeName': 'color', 'KeyType': 'HASH'},
    {'AttributeName': 'size', 'KeyType': 'RANGE'}
]
gsi_projection = {
    'ProjectionType': 'ALL'
}

# Create the GSI on the table
dynamodb.update_table(
    TableName=table_name,
    AttributeDefinitions=attribute_definitions,
    GlobalSecondaryIndexUpdates=[
        {
            'Create': {
                'IndexName': gsi_name,
                'KeySchema': gsi_key_schema,
                'Projection': gsi_projection,
                'ProvisionedThroughput': {
                    'ReadCapacityUnits': 5,
                    'WriteCapacityUnits': 5
                }
            }
        }
    ]
)

# Wait for the GSI to be created
waiter = dynamodb.get_waiter('index_exists')
waiter.wait(TableName=table_name, IndexName=gsi_name)

# Execute a query using the GSI
response = dynamodb.query(
    TableName=table_name,
    IndexName=gsi_name,
    KeyConditionExpression='color = :color and size = :size',
    ExpressionAttributeValues={
        ':color': {'S': 'red'},
        ':size': {'S': 'medium'}
    }
)

# Print the query results
print(response['Items'])
