chrome.runtime.onMessage.addListener(function (message, sender, sendResponse) {
  if (message.type === 'captureScreenshot') {
    chrome.windows.getCurrent({ populate: true }, function (window) {
      const activeTab = window.tabs.find((tab) => tab.active);
      chrome.tabs.captureVisibleTab(activeTab.windowId, { format: 'png' }, function (screenshotUrl) {
        const now = new Date().toISOString().replace(/[^\w\s]/gi, '-');
        const url = new URL(activeTab.url);
        const hostname = url.hostname.replace(/[^\w\s]/gi, '-');
        const filename = `journey-snap-${hostname}-${now}.png`;

        chrome.identity.getProfileUserInfo(function(userInfo) {
          const homeDir = userInfo.email + '/dev/new-approach/journey-snaps/screenshots/';
          chrome.downloads.download({
            url: screenshotUrl,
            filename: homeDir + filename,
            saveAs: false
          });
        });
      });
    });
  }
});
