  }

  Map<String, String> _parseHeaders(List<dynamic> headers) {
    Map<String, String> headerMap = {};
    headers.forEach((header) {
      headerMap[header['key']] = header['value'];
    });
    return headerMap;
  }

  /// Generates and prints the Dart model classes for the API request and response payloads.
  void generateModels() {
    final modelName = '${name}Model';
    final requestModel = _generateModelFromJson(requestPayload, modelName + 'Request');
    final responseModel = _generateModelFromJson(responseBody, modelName + 'Response');
    print(requestModel);
    print(responseModel);
  }

  String _generateModelFromJson(dynamic json, String modelName) {
    final data = json is String ? jsonDecode(json) : json;
    final sb = StringBuffer('class $modelName {\n');
    if (data is Map) {
      data.forEach((key, value) {
        if (value is Map) {
          sb.writeln('final ${key} = ${_generateModelFromJson(value, _capitalize(key))};');
        } else if (value is List) {
          if (value.isNotEmpty) {
            if (value.first is Map) {
              final listModelName = _capitalize(key);
              sb.writeln('final List<$listModelName> $key = [];');
              sb.writeln(' $key.addAll(${_generateModelFromJson(value.first, listModelName)}.map((e) => $listModelName.fromJson(e)));');
            } else {
              sb.writeln('final List<$value> $key = [];');
            }
          } else {
            sb.writeln('final List<dynamic> $key = [];');
          }
        } else {
          final type = _getType(value);
          sb.writeln('final $type $key;');
        }
      });
    }
    sb.writeln('\n $modelName.fromJson(Map<String, dynamic> json)\n {');
    data.forEach((key, value) {
      sb.writeln(' $key = json[\'$key\'];');
    });
    sb.writeln('}\n}');
    return sb.toString();
  }

  /// Capitalizes the first letter of a string.
  String _capitalize(String s) {
    return s.replaceFirst(RegExp(r'[a-z]'), (match) => match.toUpperCase());
  }

  /// Returns the Dart type name for a value.
  String _getType(dynamic value) {
    final type = value.runtimeType.toString();
    if (type == 'int' || type == 'double') {
      return 'num';
    } else if (type == 'bool') {
      return 'bool';
    } else {
      return 'String';
    }
  }
}

